package com.home.commonGame.net.serverRequest.scene.base;
import com.home.commonBase.constlist.generate.ServerMessageType;
import com.home.commonGame.net.serverRequest.scene.base.GameToSceneServerRequest;
import com.home.shine.bytes.BytesWriteStream;
import com.home.shine.control.BytesControl;
import com.home.shine.support.DataWriter;
import com.home.shine.support.pool.DataPool;

/** 玩家Game服到场景服消息(generated by shine) */
public class PlayerGameToSceneServerRequest extends GameToSceneServerRequest
{
	/** 数据类型ID */
	public static final int dataID=ServerMessageType.PlayerGameToScene;
	
	public long playerID;
	
	public PlayerGameToSceneServerRequest()
	{
		_dataID=ServerMessageType.PlayerGameToScene;
	}
	
	@Override
	protected void copyData()
	{
		super.copyData();
	}
	
	/** 获取数据类名 */
	@Override
	public String getDataClassName()
	{
		return "PlayerGameToSceneServerRequest";
	}
	
	/** 写入字节流(简版) */
	@Override
	protected void toWriteBytesSimple(BytesWriteStream stream)
	{
		super.toWriteBytesSimple(stream);
		
		stream.writeLong(this.playerID);
		
	}
	
	/** 转文本输出 */
	@Override
	protected void toWriteDataString(DataWriter writer)
	{
		super.toWriteDataString(writer);
		
		writer.writeTabs();
		writer.sb.append("playerID");
		writer.sb.append(':');
		writer.sb.append(this.playerID);
		
		writer.writeEnter();
	}
	
	/** 回池 */
	@Override
	protected void toRelease(DataPool pool)
	{
		super.toRelease(pool);
		
		this.playerID=0L;
	}
	
	/** 创建实例 */
	public static PlayerGameToSceneServerRequest create(long playerID)
	{
		PlayerGameToSceneServerRequest re=(PlayerGameToSceneServerRequest)BytesControl.createRequest(dataID);
		re.playerID=playerID;
		return re;
	}
	
}
