package com.home.commonGame.net.serverResponse.center.activity;
import com.home.commonBase.constlist.generate.ServerMessageType;
import com.home.commonGame.global.GameC;
import com.home.commonGame.net.serverResponse.center.base.CenterToGameServerResponse;
import com.home.shine.bytes.BytesReadStream;
import com.home.shine.support.DataWriter;
import com.home.shine.support.pool.DataPool;

/** 改变活动强制关闭消息(generated by shine) */
public class ChangeActivityForceCloseToGameServerResponse extends CenterToGameServerResponse
{
	/** 数据类型ID */
	public static final int dataID=ServerMessageType.ChangeActivityForceCloseToGame;
	
	public int id;
	
	/** 是否强制关闭 */
	public boolean isForceClosed;
	
	public ChangeActivityForceCloseToGameServerResponse()
	{
		_dataID=ServerMessageType.ChangeActivityForceCloseToGame;
	}
	
	/** 读取字节流(简版) */
	@Override
	protected void toReadBytesSimple(BytesReadStream stream)
	{
		super.toReadBytesSimple(stream);
		
		this.id=stream.readInt();
		
		this.isForceClosed=stream.readBoolean();
		
	}
	
	/** 执行 */
	@Override
	protected void execute()
	{
		GameC.global.activity.onForceCloseActivityFromCenter(id,isForceClosed);
	}
	
	/** 获取数据类名 */
	@Override
	public String getDataClassName()
	{
		return "ChangeActivityForceCloseToGameServerResponse";
	}
	
	/** 转文本输出 */
	@Override
	protected void toWriteDataString(DataWriter writer)
	{
		super.toWriteDataString(writer);
		
		writer.writeTabs();
		writer.sb.append("id");
		writer.sb.append(':');
		writer.sb.append(this.id);
		
		writer.writeEnter();
		writer.writeTabs();
		writer.sb.append("isForceClosed");
		writer.sb.append(':');
		writer.sb.append(this.isForceClosed);
		
		writer.writeEnter();
	}
	
	/** 回池 */
	@Override
	protected void toRelease(DataPool pool)
	{
		super.toRelease(pool);
		
		this.id=0;
		this.isForceClosed=false;
	}
	
}
