package com.home.commonGame.net.serverResponse.center.func.auction;
import com.home.commonBase.constlist.generate.ServerMessageType;
import com.home.commonGame.global.GameC;
import com.home.commonGame.net.serverResponse.center.func.base.FuncCenterToGameServerResponse;
import com.home.shine.bytes.BytesReadStream;
import com.home.shine.support.DataWriter;
import com.home.shine.support.collection.IntIntMap;
import com.home.shine.support.pool.DataPool;

/** 刷新拍卖行物品价格消息到逻辑服(generated by shine) */
public class FuncRefreshAuctionItemPriceToGameServerResponse extends FuncCenterToGameServerResponse
{
	/** 数据类型ID */
	public static final int dataID=ServerMessageType.FuncRefreshAuctionItemPriceToGame;
	
	/** 物品价格列表 */
	public IntIntMap itemPriceDic;
	
	public FuncRefreshAuctionItemPriceToGameServerResponse()
	{
		_dataID=ServerMessageType.FuncRefreshAuctionItemPriceToGame;
	}
	
	/** 获取数据类名 */
	@Override
	public String getDataClassName()
	{
		return "FuncRefreshAuctionItemPriceToGameServerResponse";
	}
	
	/** 读取字节流(简版) */
	@Override
	protected void toReadBytesSimple(BytesReadStream stream)
	{
		super.toReadBytesSimple(stream);
		
		int itemPriceDicLen=stream.readLen();
		if(this.itemPriceDic!=null)
		{
			this.itemPriceDic.clear();
			this.itemPriceDic.ensureCapacity(itemPriceDicLen);
		}
		else
		{
			this.itemPriceDic=new IntIntMap(itemPriceDicLen);
		}
		
		IntIntMap itemPriceDicT=this.itemPriceDic;
		for(int itemPriceDicI=itemPriceDicLen-1;itemPriceDicI>=0;--itemPriceDicI)
		{
			int itemPriceDicK;
			int itemPriceDicV;
			itemPriceDicK=stream.readInt();
			
			itemPriceDicV=stream.readInt();
			
			itemPriceDicT.put(itemPriceDicK,itemPriceDicV);
		}
		
	}
	
	/** 转文本输出 */
	@Override
	protected void toWriteDataString(DataWriter writer)
	{
		super.toWriteDataString(writer);
		
		writer.writeTabs();
		writer.sb.append("itemPriceDic");
		writer.sb.append(':');
		writer.sb.append("Map<int,int>");
		if(this.itemPriceDic!=null)
		{
			writer.sb.append('(');
			writer.sb.append(this.itemPriceDic.size());
			writer.sb.append(')');
			writer.writeEnter();
			writer.writeLeftBrace();
			if(!this.itemPriceDic.isEmpty())
			{
				int itemPriceDicKFreeValue=this.itemPriceDic.getFreeValue();
				long[] itemPriceDicKTable=this.itemPriceDic.getTable();
				long itemPriceDicKEntry;
				for(int itemPriceDicKI=itemPriceDicKTable.length-1;itemPriceDicKI>=0;--itemPriceDicKI)
				{
					itemPriceDicKEntry=itemPriceDicKTable[itemPriceDicKI];
					int itemPriceDicK=(int)itemPriceDicKEntry;
					if(itemPriceDicK!=itemPriceDicKFreeValue)
					{
						int itemPriceDicV=(int)(itemPriceDicKEntry>>>32);
						writer.writeTabs();
						writer.sb.append(itemPriceDicK);
						
						writer.sb.append(':');
						writer.sb.append(itemPriceDicV);
						
						writer.writeEnter();
					}
				}
			}
			writer.writeRightBrace();
		}
		else
		{
			writer.sb.append("=null");
		}
		
		writer.writeEnter();
	}
	
	/** 执行 */
	@Override
	protected void execute()
	{
		GameC.global.func.getAuctionToolForCenter(funcID).refreshItemPrice(itemPriceDic);
	}
	
	/** 回池 */
	@Override
	protected void toRelease(DataPool pool)
	{
		super.toRelease(pool);
		
		this.itemPriceDic=null;
	}
	
}
