package com.home.commonGame.net.serverResponse.center.func.match;
import com.home.commonBase.constlist.generate.ServerMessageType;
import com.home.commonGame.global.GameC;
import com.home.commonGame.net.serverResponse.center.base.PlayerToGameServerResponse;
import com.home.shine.bytes.BytesReadStream;
import com.home.shine.support.DataWriter;
import com.home.shine.support.pool.DataPool;

/** 匹配超时到游戏服(generated by shine) */
public class FuncMatchTimeOutToGameServerResponse extends PlayerToGameServerResponse
{
	/** 数据类型ID */
	public static final int dataID=ServerMessageType.FuncMatchTimeOutToGame;
	
	/** 功能ID */
	public int funcID;
	
	public FuncMatchTimeOutToGameServerResponse()
	{
		_dataID=ServerMessageType.FuncMatchTimeOutToGame;
	}
	
	/** 读取字节流(简版) */
	@Override
	protected void toReadBytesSimple(BytesReadStream stream)
	{
		super.toReadBytesSimple(stream);
		
		this.funcID=stream.readInt();
		
	}
	
	/** 执行 */
	@Override
	protected void execute()
	{
		GameC.global.func.getMatchToolForCenter(funcID).onMatchTimeOutFromCenter(me);
	}
	
	/** 获取数据类名 */
	@Override
	public String getDataClassName()
	{
		return "FuncMatchTimeOutToGameServerResponse";
	}
	
	/** 转文本输出 */
	@Override
	protected void toWriteDataString(DataWriter writer)
	{
		super.toWriteDataString(writer);
		
		writer.writeTabs();
		writer.sb.append("funcID");
		writer.sb.append(':');
		writer.sb.append(this.funcID);
		
		writer.writeEnter();
	}
	
	/** 回池 */
	@Override
	protected void toRelease(DataPool pool)
	{
		super.toRelease(pool);
		
		this.funcID=0;
	}
	
}
