package com.home.commonGame.net.serverResponse.center.system;
import com.home.commonBase.constlist.generate.InfoCodeType;
import com.home.commonBase.constlist.generate.ServerMessageType;
import com.home.commonGame.global.GameC;
import com.home.commonGame.net.serverResponse.center.base.CenterToGameServerResponse;
import com.home.shine.bytes.BytesReadStream;
import com.home.shine.support.DataWriter;
import com.home.shine.support.pool.DataPool;

/** 推送事务执行失败(找不到game服)(generated by shine) */
public class SendWorkFailedServerResponse extends CenterToGameServerResponse
{
	/** 数据类型ID */
	public static final int dataID=ServerMessageType.SendWorkFailed;
	
	/** 发送者序号 */
	public int senderIndex;
	
	/** 实例ID */
	public long instanceID;
	
	public SendWorkFailedServerResponse()
	{
		_dataID=ServerMessageType.SendWorkFailed;
	}
	
	/** 获取数据类名 */
	@Override
	public String getDataClassName()
	{
		return "SendWorkFailedServerResponse";
	}
	
	/** 读取字节流(简版) */
	@Override
	protected void toReadBytesSimple(BytesReadStream stream)
	{
		super.toReadBytesSimple(stream);
		
		this.senderIndex=stream.readInt();
		
		this.instanceID=stream.readLong();
		
	}
	
	/** 执行 */
	@Override
	protected void execute()
	{
		GameC.main.onReceiptWork(senderIndex,instanceID,InfoCodeType.WorkError);
	}
	
	/** 转文本输出 */
	@Override
	protected void toWriteDataString(DataWriter writer)
	{
		super.toWriteDataString(writer);
		
		writer.writeTabs();
		writer.sb.append("senderIndex");
		writer.sb.append(':');
		writer.sb.append(this.senderIndex);
		
		writer.writeEnter();
		writer.writeTabs();
		writer.sb.append("instanceID");
		writer.sb.append(':');
		writer.sb.append(this.instanceID);
		
		writer.writeEnter();
	}
	
	/** 回池 */
	@Override
	protected void toRelease(DataPool pool)
	{
		super.toRelease(pool);
		
		this.senderIndex=0;
		this.instanceID=0L;
	}
	
}
