package com.home.commonGame.net.serverResponse.game.func.auction;
import com.home.commonBase.constlist.generate.ServerMessageType;
import com.home.commonGame.global.GameC;
import com.home.commonGame.net.serverResponse.game.func.base.FuncToGameServerResponse;
import com.home.shine.bytes.BytesReadStream;
import com.home.shine.constlist.ThreadType;
import com.home.shine.control.ThreadControl;
import com.home.shine.support.DataWriter;
import com.home.shine.support.pool.DataPool;

/** 取消上架拍卖行到源服(generated by shine) */
public class FuncSendAuctionCancelSellItemToSourceGameServerResponse extends FuncToGameServerResponse
{
	/** 数据类型ID */
	public static final int dataID=ServerMessageType.FuncSendAuctionCancelSellItemToSourceGame;
	
	public long playerID;
	
	public int reason;
	
	public long instanceID;
	
	public FuncSendAuctionCancelSellItemToSourceGameServerResponse()
	{
		_dataID=ServerMessageType.FuncSendAuctionCancelSellItemToSourceGame;
	}
	
	/** 获取数据类名 */
	@Override
	public String getDataClassName()
	{
		return "FuncSendAuctionCancelSellItemToSourceGameServerResponse";
	}
	
	/** 读取字节流(简版) */
	@Override
	protected void toReadBytesSimple(BytesReadStream stream)
	{
		super.toReadBytesSimple(stream);
		
		this.playerID=stream.readLong();
		
		this.instanceID=stream.readLong();
		
		this.reason=stream.readInt();
		
	}
	
	/** 转文本输出 */
	@Override
	protected void toWriteDataString(DataWriter writer)
	{
		super.toWriteDataString(writer);
		
		writer.writeTabs();
		writer.sb.append("playerID");
		writer.sb.append(':');
		writer.sb.append(this.playerID);
		
		writer.writeEnter();
		writer.writeTabs();
		writer.sb.append("instanceID");
		writer.sb.append(':');
		writer.sb.append(this.instanceID);
		
		writer.writeEnter();
		writer.writeTabs();
		writer.sb.append("reason");
		writer.sb.append(':');
		writer.sb.append(this.reason);
		
		writer.writeEnter();
	}
	
	@Override
	public void dispatch()
	{
		_threadType=ThreadType.Pool;
		_poolIndex=(int)instanceID & ThreadControl.poolThreadNumMark;
		doDispatch();
	}
	
	/** 执行 */
	@Override
	protected void execute()
	{
		GameC.global.func.getAuctionTool(funcID).getExecutor(_poolIndex).removeSellItem(playerID,instanceID,reason);
	}
	
	/** 回池 */
	@Override
	protected void toRelease(DataPool pool)
	{
		super.toRelease(pool);
		
		this.playerID=0L;
		this.instanceID=0L;
		this.reason=0;
	}
	
}
