package com.home.commonGame.net.serverResponse.game.func.rank;
import com.home.commonBase.constlist.generate.ServerMessageType;
import com.home.commonBase.data.social.rank.RankSimpleData;
import com.home.commonGame.net.serverResponse.game.func.base.FuncPlayerRoleGroupGameToGameServerResponse;
import com.home.shine.bytes.BytesReadStream;
import com.home.shine.support.DataWriter;
import com.home.shine.support.pool.DataPool;

/** 更新排行(玩家群)消息(generated by shine) */
public class FuncRefreshRankForRoleGroupServerResponse extends FuncPlayerRoleGroupGameToGameServerResponse
{
	/** 排行榜的功能ID */
	public int rankFuncID;
	
	/** 数据类型ID */
	public static final int dataID=ServerMessageType.FuncRefreshRankForRoleGroup;
	
	/** 排行(-1为移除排行) */
	public int rank;
	
	/** 排行值 */
	public long value;
	
	public FuncRefreshRankForRoleGroupServerResponse()
	{
		_dataID=ServerMessageType.FuncRefreshRankForRoleGroup;
	}
	
	/** 获取数据类名 */
	@Override
	public String getDataClassName()
	{
		return "FuncRefreshRankForRoleGroupServerResponse";
	}
	
	/** 读取字节流(简版) */
	@Override
	protected void toReadBytesSimple(BytesReadStream stream)
	{
		super.toReadBytesSimple(stream);
		
		this.rankFuncID=stream.readInt();
		
		this.rank=stream.readInt();
		
		this.value=stream.readLong();
		
	}
	
	/** 转文本输出 */
	@Override
	protected void toWriteDataString(DataWriter writer)
	{
		super.toWriteDataString(writer);
		
		writer.writeTabs();
		writer.sb.append("rankFuncID");
		writer.sb.append(':');
		writer.sb.append(this.rankFuncID);
		
		writer.writeEnter();
		writer.writeTabs();
		writer.sb.append("rank");
		writer.sb.append(':');
		writer.sb.append(this.rank);
		
		writer.writeEnter();
		writer.writeTabs();
		writer.sb.append("value");
		writer.sb.append(':');
		writer.sb.append(this.value);
		
		writer.writeEnter();
	}
	
	/** 执行 */
	@Override
	protected void execute()
	{
		roleGroup.onRefreshRank(rankFuncID,rank,value);
	}
	
	/** 回池 */
	@Override
	protected void toRelease(DataPool pool)
	{
		super.toRelease(pool);
		
		this.rankFuncID=0;
		this.rank=0;
		this.value=0L;
	}
	
}
