package com.home.commonGame.net.serverResponse.game.func.roleGroup;
import com.home.commonBase.constlist.generate.ServerMessageType;
import com.home.commonGame.net.serverResponse.game.func.base.FuncRoleGroupToGameServerResponse;
import com.home.shine.bytes.BytesReadStream;
import com.home.shine.support.DataWriter;
import com.home.shine.support.pool.DataPool;

/** 离开玩家群到逻辑服(generated by shine) */
public class FuncLeaveRoleGroupToGameServerResponse extends FuncRoleGroupToGameServerResponse
{
	public long playerID;
	
	/** 数据类型ID */
	public static final int dataID=ServerMessageType.FuncLeaveRoleGroupToGame;
	
	public FuncLeaveRoleGroupToGameServerResponse()
	{
		_dataID=ServerMessageType.FuncLeaveRoleGroupToGame;
	}
	
	/** 获取数据类名 */
	@Override
	public String getDataClassName()
	{
		return "FuncLeaveRoleGroupToGameServerResponse";
	}
	
	/** 执行 */
	@Override
	protected void execute()
	{
		roleGroup.playerLeave(playerID);
	}
	
	/** 读取字节流(简版) */
	@Override
	protected void toReadBytesSimple(BytesReadStream stream)
	{
		super.toReadBytesSimple(stream);
		
		this.playerID=stream.readLong();
		
	}
	
	/** 转文本输出 */
	@Override
	protected void toWriteDataString(DataWriter writer)
	{
		super.toWriteDataString(writer);
		
		writer.writeTabs();
		writer.sb.append("playerID");
		writer.sb.append(':');
		writer.sb.append(this.playerID);
		
		writer.writeEnter();
	}
	
	/** 回池 */
	@Override
	protected void toRelease(DataPool pool)
	{
		super.toRelease(pool);
		
		this.playerID=0L;
	}
	
}
