package com.home.commonGame.net.serverResponse.game.func.roleGroup;
import com.home.commonBase.constlist.generate.ServerMessageType;
import com.home.commonBase.data.social.roleGroup.RoleGroupChangeData;
import com.home.commonGame.global.GameC;
import com.home.commonGame.net.serverResponse.game.func.base.FuncToGameServerResponse;
import com.home.shine.bytes.BytesReadStream;
import com.home.shine.support.DataWriter;
import com.home.shine.support.pool.DataPool;

/** 玩家群简版数据变更到逻辑服(generated by shine) */
public class FuncRoleGroupChangeSimpleToGameServerResponse extends FuncToGameServerResponse
{
	/** 数据类型ID */
	public static final int dataID=ServerMessageType.FuncRoleGroupChangeSimpleToGame;
	
	public long groupID;
	
	/** 改变数据 */
	public RoleGroupChangeData changeData;
	
	public FuncRoleGroupChangeSimpleToGameServerResponse()
	{
		_dataID=ServerMessageType.FuncRoleGroupChangeSimpleToGame;
	}
	
	/** 获取数据类名 */
	@Override
	public String getDataClassName()
	{
		return "FuncRoleGroupChangeSimpleToGameServerResponse";
	}
	
	/** 读取字节流(简版) */
	@Override
	protected void toReadBytesSimple(BytesReadStream stream)
	{
		super.toReadBytesSimple(stream);
		
		this.groupID=stream.readLong();
		
		this.changeData=(RoleGroupChangeData)stream.readDataSimpleNotNull();
		
	}
	
	/** 转文本输出 */
	@Override
	protected void toWriteDataString(DataWriter writer)
	{
		super.toWriteDataString(writer);
		
		writer.writeTabs();
		writer.sb.append("groupID");
		writer.sb.append(':');
		writer.sb.append(this.groupID);
		
		writer.writeEnter();
		writer.writeTabs();
		writer.sb.append("changeData");
		writer.sb.append(':');
		if(this.changeData!=null)
		{
			this.changeData.writeDataString(writer);
		}
		else
		{
			writer.sb.append("RoleGroupChangeData=null");
		}
		
		writer.writeEnter();
	}
	
	/** 执行 */
	@Override
	protected void execute()
	{
		GameC.global.func.getRoleGroupTool(funcID).onRoleGroupSimpleChange(groupID,changeData);
	}
	
	/** 回池 */
	@Override
	protected void toRelease(DataPool pool)
	{
		super.toRelease(pool);
		
		this.groupID=0L;
		this.changeData=null;
	}
	
}
