package com.home.commonGame.net.serverResponse.game.func.roleGroup;
import com.home.commonBase.constlist.generate.ServerMessageType;
import com.home.commonGame.net.serverResponse.game.func.base.FuncPlayerGameToGameServerResponse;
import com.home.shine.bytes.BytesReadStream;
import com.home.shine.support.DataWriter;
import com.home.shine.support.pool.DataPool;

/** 推送加入玩家群类消息结果(generated by shine) */
public class FuncSendRoleGroupJoinResultServerResponse extends FuncPlayerGameToGameServerResponse
{
	/** 数据类型ID */
	public static final int dataID=ServerMessageType.FuncSendRoleGroupJoinResult;
	
	public long groupID;
	
	public boolean success;
	
	public FuncSendRoleGroupJoinResultServerResponse()
	{
		_dataID=ServerMessageType.FuncSendRoleGroupJoinResult;
	}
	
	/** 获取数据类名 */
	@Override
	public String getDataClassName()
	{
		return "FuncSendRoleGroupJoinResultServerResponse";
	}
	
	/** 读取字节流(简版) */
	@Override
	protected void toReadBytesSimple(BytesReadStream stream)
	{
		super.toReadBytesSimple(stream);
		
		this.groupID=stream.readLong();
		
		this.success=stream.readBoolean();
		
	}
	
	/** 转文本输出 */
	@Override
	protected void toWriteDataString(DataWriter writer)
	{
		super.toWriteDataString(writer);
		
		writer.writeTabs();
		writer.sb.append("groupID");
		writer.sb.append(':');
		writer.sb.append(this.groupID);
		
		writer.writeEnter();
		writer.writeTabs();
		writer.sb.append("success");
		writer.sb.append(':');
		writer.sb.append(this.success);
		
		writer.writeEnter();
	}
	
	/** 执行 */
	@Override
	protected void execute()
	{
		me.func.getRoleGroupTool(funcID).onJoinResult(success);
	}
	
	/** 回池 */
	@Override
	protected void toRelease(DataPool pool)
	{
		super.toRelease(pool);
		
		this.groupID=0L;
		this.success=false;
	}
	
}
