package com.home.commonGame.net.serverResponse.game.social;
import com.home.commonBase.constlist.generate.ServerMessageType;
import com.home.commonBase.data.social.chat.RoleChatData;
import com.home.commonGame.net.serverResponse.game.base.PlayerGameToGameServerResponse;
import com.home.shine.bytes.BytesReadStream;
import com.home.shine.support.DataWriter;
import com.home.shine.support.pool.DataPool;

/** 推送玩家聊天消息(generated by shine) */
public class SendPlayerChatToPlayerServerResponse extends PlayerGameToGameServerResponse
{
	/** 数据类型ID */
	public static final int dataID=ServerMessageType.SendPlayerChatToPlayer;
	
	/** 聊天数据 */
	public RoleChatData chatData;
	
	/** 频道 */
	public int channel;
	
	/** 第二索引 */
	public long key;
	
	public SendPlayerChatToPlayerServerResponse()
	{
		_dataID=ServerMessageType.SendPlayerChatToPlayer;
	}
	
	/** 获取数据类名 */
	@Override
	public String getDataClassName()
	{
		return "SendPlayerChatToPlayerServerResponse";
	}
	
	/** 读取字节流(简版) */
	@Override
	protected void toReadBytesSimple(BytesReadStream stream)
	{
		super.toReadBytesSimple(stream);
		
		this.chatData=(RoleChatData)stream.readDataSimpleNotNull();
		
		this.channel=stream.readInt();
		
		this.key=stream.readLong();
		
	}
	
	/** 转文本输出 */
	@Override
	protected void toWriteDataString(DataWriter writer)
	{
		super.toWriteDataString(writer);
		
		writer.writeTabs();
		writer.sb.append("chatData");
		writer.sb.append(':');
		if(this.chatData!=null)
		{
			this.chatData.writeDataString(writer);
		}
		else
		{
			writer.sb.append("RoleChatData=null");
		}
		
		writer.writeEnter();
		writer.writeTabs();
		writer.sb.append("channel");
		writer.sb.append(':');
		writer.sb.append(this.channel);
		
		writer.writeEnter();
		writer.writeTabs();
		writer.sb.append("key");
		writer.sb.append(':');
		writer.sb.append(this.key);
		
		writer.writeEnter();
	}
	
	/** 执行 */
	@Override
	protected void execute()
	{
		me.social.onReceiveChat(chatData,channel,key);
	}
	
	/** 回池 */
	@Override
	protected void toRelease(DataPool pool)
	{
		super.toRelease(pool);
		
		this.chatData=null;
		this.channel=0;
		this.key=0L;
	}
	
}
