package com.home.commonGame.net.serverResponse.scene.login;
import com.home.commonBase.constlist.generate.ServerMessageType;
import com.home.commonBase.data.scene.scene.SceneServerExitData;
import com.home.commonGame.global.GameC;
import com.home.commonGame.net.serverResponse.scene.base.PlayerSceneToGameServerResponse;
import com.home.shine.bytes.BytesReadStream;
import com.home.shine.ctrl.Ctrl;
import com.home.shine.support.DataWriter;
import com.home.shine.support.pool.DataPool;

/** 玩家离开场景完毕到game服(generated by shine) */
public class PlayerLeaveSceneOverToGameServerResponse extends PlayerSceneToGameServerResponse
{
	/** 数据类型ID */
	public static final int dataID=ServerMessageType.PlayerLeaveSceneOverToGame;
	
	/** 携带数据 */
	public SceneServerExitData data;
	
	public PlayerLeaveSceneOverToGameServerResponse()
	{
		_dataID=ServerMessageType.PlayerLeaveSceneOverToGame;
	}
	
	/** 获取数据类名 */
	@Override
	public String getDataClassName()
	{
		return "PlayerLeaveSceneOverToGameServerResponse";
	}
	
	/** 读取字节流(简版) */
	@Override
	protected void toReadBytesSimple(BytesReadStream stream)
	{
		super.toReadBytesSimple(stream);
		
		this.data=(SceneServerExitData)stream.readDataSimpleNotNull();
		
	}
	
	/** 转文本输出 */
	@Override
	protected void toWriteDataString(DataWriter writer)
	{
		super.toWriteDataString(writer);
		
		writer.writeTabs();
		writer.sb.append("data");
		writer.sb.append(':');
		if(this.data!=null)
		{
			this.data.writeDataString(writer);
		}
		else
		{
			writer.sb.append("SceneServerExitData=null");
		}
		
		writer.writeEnter();
	}
	
	/** 回池 */
	@Override
	protected void toRelease(DataPool pool)
	{
		super.toRelease(pool);
		
		this.data=null;
	}
	
	@Override
	protected void preExecute()
	{
		me=GameC.main.getPlayerByIDT(playerID);
		
		if(me!=null)
		{
			doExecute();
		}
		else
		{
			Ctrl.warnLog("收到PlayerLeaveSceneOverToGameServerResponse时,player丢失",getDataClassName(),playerID);
		}
	}
	
	/** 执行 */
	@Override
	protected void execute()
	{
		me.addFunc(()->
		{
			me.scene.onLeaveSceneOver(data);
		});
	}
	
}
