package com.home.commonGame.net.serverResponse.scene.system;
import com.home.commonBase.constlist.generate.ServerMessageType;
import com.home.commonGame.global.GameC;
import com.home.commonGame.net.serverResponse.scene.base.SceneToGameServerResponse;
import com.home.shine.bytes.BytesReadStream;
import com.home.shine.constlist.SocketType;
import com.home.shine.net.socket.SendSocket;
import com.home.shine.support.DataWriter;
import com.home.shine.support.pool.DataPool;

/** (generated by shine) */
public class ReBeGameToSceneServerResponse extends SceneToGameServerResponse
{
	/** 在线角色数 */
	public int playerNum;
	
	/** 数据类型ID */
	public static final int dataID=ServerMessageType.ReBeGameToScene;
	
	public ReBeGameToSceneServerResponse()
	{
		_dataID=ServerMessageType.ReBeGameToScene;
	}
	
	/** 获取数据类名 */
	@Override
	public String getDataClassName()
	{
		return "ReBeGameToSceneServerResponse";
	}
	
	/** 执行 */
	@Override
	protected void execute()
	{
		GameC.server.reBack(SocketType.Scene,((SendSocket)socket).sendID);
		GameC.main.setSceneServerPlayerNum(((SendSocket)socket).sendID,playerNum);
		GameC.server.checkNext();
	}
	
	/** 读取字节流(简版) */
	@Override
	protected void toReadBytesSimple(BytesReadStream stream)
	{
		super.toReadBytesSimple(stream);
		
		this.playerNum=stream.readInt();
		
	}
	
	/** 转文本输出 */
	@Override
	protected void toWriteDataString(DataWriter writer)
	{
		super.toWriteDataString(writer);
		
		writer.writeTabs();
		writer.sb.append("playerNum");
		writer.sb.append(':');
		writer.sb.append(this.playerNum);
		
		writer.writeEnter();
	}
	
	/** 回池 */
	@Override
	protected void toRelease(DataPool pool)
	{
		super.toRelease(pool);
		
		this.playerNum=0;
	}
	
}
