package com.home.commonGame.part.player.part;
import com.home.commonBase.baseData.BaseClientPartData;
import com.home.commonBase.global.Global;
import com.home.commonBase.part.player.clientData.TeamClientPartData;
import com.home.commonBase.part.player.data.TeamPartData;
import com.home.commonGame.global.GameC;
import com.home.commonGame.logic.team.PlayerTeam;
import com.home.commonGame.logic.team.PlayerTeamTool;
import com.home.commonGame.part.player.base.PlayerBasePart;
import com.home.shine.data.BaseData;

/** 组队(generated by shine) */
public class TeamPart extends PlayerBasePart
{
	/** 数据 */
	private TeamPartData _d;
	
	/** 玩家队伍工具 */
	private PlayerTeamTool _tool;
	
	@Override
	public void setData(BaseData data)
	{
		super.setData(data);
		
		_d=(TeamPartData)data;
	}
	
	/** 获取数据 */
	public TeamPartData getPartData()
	{
		return _d;
	}
	
	@Override
	protected BaseData createPartData()
	{
		return new TeamPartData();
	}
	
	@Override
	protected BaseClientPartData createClientData()
	{
		return new TeamClientPartData();
	}
	
	/** 写客户端数据(copyServer过后的) */
	@Override
	protected void writeClientData(BaseClientPartData data)
	{
		toWriteClientData((TeamClientPartData)data);
	}
	
	/** 写客户端数据具体执行(已执行过copyServer了,就是说CPO中与SPO同名同类型的属性无需再赋值) */
	private void toWriteClientData(TeamClientPartData data)
	{
		
	}
	
	/** 构造函数(只在new后调用一次,再次从池中取出不会调用) */
	@Override
	public void construct()
	{
		if(Global.teamRoleGroupID>0)
		{
			me.func.registFuncTool(_tool=GameC.factory.createPlayerTeamTool());
		}
	}
	
	/** 初始化(创建后刚调用,与dispose成对) */
	@Override
	public void init()
	{
		
	}
	
	/** 析构(回池前调用,与init成对) */
	@Override
	public void dispose()
	{
		
	}
	
	/** 从库中读完数据后(做数据的补充解析)(onNewCreate后也会调用一次)(主线程) */
	@Override
	public void afterReadData()
	{
		
	}
	
	/** 新创建时(该主对象在服务器上第一次被创建时的动作(一生只一次),(只对数据赋值就好,自定义数据构造的部分写到afterReadData里,因为这个完事儿就会回调到)) */
	@Override
	public void onNewCreate()
	{
		
	}
	
	/** 构造数据前 */
	@Override
	protected void beforeMakeData()
	{
		
	}
	
	/** 功能开启(id:功能ID) */
	@Override
	public void onFunctionOpen(int id)
	{
		
	}
	
	/** 功能关闭(id:功能ID) */
	@Override
	public void onFunctionClose(int id)
	{
		
	}
	
	/** 是否有队伍 */
	public boolean hasTeam()
	{
		if(_tool==null)
			return false;
		
		return _tool.getOnlyOne()!=null;
	}
	
	/** 获取所在队伍 */
	public PlayerTeam getTeam()
	{
		if(_tool==null)
			return null;
		
		return _tool.getOnlyOne();
	}
	
	/** 获取玩家队伍工具 */
	public PlayerTeamTool getPlayerTeamTool()
	{
		return _tool;
	}
	
}
