package com.home.commonLogin.net.httpResponse;
import com.home.commonBase.data.login.ClientLoginServerInfoData;
import com.home.commonLogin.constlist.generate.LoginHttpResponseType;
import com.home.commonLogin.global.LoginC;
import com.home.commonLogin.net.base.LoginHttpResponse;
import com.home.commonLogin.net.httpResponseResult.ClientLoginSelectResult;
import com.home.shine.bytes.BytesReadStream;

/** 登录选择消息(generated by shine) */
public class ClientLoginSelectHttpResponse extends LoginHttpResponse
{
	/** 数据类型ID */
	public static final int dataID=LoginHttpResponseType.ClientLoginSelect;
	
	/** 登录令牌 */
	public int loginToken;
	
	/** 选择区服ID */
	public int areaID=-1;
	
	/** 返回值 */
	protected ClientLoginSelectResult _re=new ClientLoginSelectResult();
	
	public ClientLoginSelectHttpResponse()
	{
		_dataID=LoginHttpResponseType.ClientLoginSelect;
	}
	
	/** 读取字节流(完整版) */
	@Override
	protected void toReadBytesFull(BytesReadStream stream)
	{
		super.toReadBytesFull(stream);
		
		stream.startReadObj();
		
		this.loginToken=stream.readInt();
		
		this.areaID=stream.readInt();
		
		stream.endReadObj();
	}
	
	/** 读取字节流(简版) */
	@Override
	protected void toReadBytesSimple(BytesReadStream stream)
	{
		super.toReadBytesSimple(stream);
		
		this.loginToken=stream.readInt();
		
		this.areaID=stream.readInt();
		
	}
	
	/** 执行 */
	@Override
	protected void execute()
	{
		LoginC.main.loginSelect(httpInstanceID,loginToken,areaID);
	}
	
	/** 收到返回方法 */
	public void reBack(ClientLoginServerInfoData info)
	{
		_re.info=info;
		resultData(_re);
	}
	
}
