package com.home.commonLogin.net.httpResponse;
import com.home.commonLogin.constlist.generate.LoginHttpResponseType;
import com.home.commonLogin.global.LoginC;
import com.home.commonLogin.net.base.LoginHttpResponse;
import com.home.shine.bytes.BytesReadStream;

/** 发送客户端流程步消息(generated by shine) */
public class SendHttpClientFlowStepHttpResponse extends LoginHttpResponse
{
	/** 数据类型ID */
	public static final int dataID=LoginHttpResponseType.SendHttpClientFlowStep;
	
	/** uid */
	public String uid="";
	
	/** 步骤 */
	public int step;
	
	public SendHttpClientFlowStepHttpResponse()
	{
		_dataID=LoginHttpResponseType.SendHttpClientFlowStep;
	}
	
	/** 读取字节流(完整版) */
	@Override
	protected void toReadBytesFull(BytesReadStream stream)
	{
		super.toReadBytesFull(stream);
		
		stream.startReadObj();
		
		this.uid=stream.readUTF();
		
		this.step=stream.readInt();
		
		stream.endReadObj();
	}
	
	/** 读取字节流(简版) */
	@Override
	protected void toReadBytesSimple(BytesReadStream stream)
	{
		super.toReadBytesSimple(stream);
		
		this.uid=stream.readUTF();
		
		this.step=stream.readInt();
		
	}
	
	/** 执行 */
	@Override
	protected void execute()
	{
		LoginC.main.addFlowLog(uid,step);
		result(true);
	}
	
}
