package com.home.commonLogin.net.serverRequest.game.system;
import com.home.commonBase.constlist.generate.ServerMessageType;
import com.home.commonLogin.net.serverRequest.game.base.LoginToGameServerRequest;
import com.home.shine.bytes.BytesWriteStream;
import com.home.shine.control.BytesControl;
import com.home.shine.support.DataWriter;
import com.home.shine.support.pool.DataPool;

/** 发送信息码来自登录服(generated by shine) */
public class SendInfoCodeFromLoginServerRequest extends LoginToGameServerRequest
{
	public int code;
	
	public long playerID;
	
	/** 数据类型ID */
	public static final int dataID=ServerMessageType.SendInfoCodeFromLogin;
	
	public SendInfoCodeFromLoginServerRequest()
	{
		_dataID=ServerMessageType.SendInfoCodeFromLogin;
	}
	
	@Override
	protected void copyData()
	{
		super.copyData();
	}
	
	/** 获取数据类名 */
	@Override
	public String getDataClassName()
	{
		return "SendInfoCodeFromLoginServerRequest";
	}
	
	/** 写入字节流(简版) */
	@Override
	protected void toWriteBytesSimple(BytesWriteStream stream)
	{
		super.toWriteBytesSimple(stream);
		
		stream.writeLong(this.playerID);
		
		stream.writeInt(this.code);
		
	}
	
	/** 转文本输出 */
	@Override
	protected void toWriteDataString(DataWriter writer)
	{
		super.toWriteDataString(writer);
		
		writer.writeTabs();
		writer.sb.append("playerID");
		writer.sb.append(':');
		writer.sb.append(this.playerID);
		
		writer.writeEnter();
		writer.writeTabs();
		writer.sb.append("code");
		writer.sb.append(':');
		writer.sb.append(this.code);
		
		writer.writeEnter();
	}
	
	/** 回池 */
	@Override
	protected void toRelease(DataPool pool)
	{
		super.toRelease(pool);
		
		this.playerID=0L;
		this.code=0;
	}
	
	/** 创建实例 */
	public static SendInfoCodeFromLoginServerRequest create(long playerID,int code)
	{
		SendInfoCodeFromLoginServerRequest re=(SendInfoCodeFromLoginServerRequest)BytesControl.createRequest(dataID);
		re.playerID=playerID;
		re.code=code;
		return re;
	}
	
}
