package com.home.commonLogin.net.serverRequest.login.login;
import com.home.commonBase.constlist.generate.ServerMessageType;
import com.home.commonBase.data.login.ClientLoginData;
import com.home.commonLogin.net.serverRequest.login.base.LoginToLoginServerRequest;
import com.home.shine.bytes.BytesWriteStream;
import com.home.shine.control.BytesControl;
import com.home.shine.support.DataWriter;
import com.home.shine.support.pool.DataPool;

/** 客户端登陆转移消息(generated by shine) */
public class ClientLoginTransferServerRequest extends LoginToLoginServerRequest
{
	/** 数据类型ID */
	public static final int dataID=ServerMessageType.ClientLoginTransfer;
	
	/** http消息ID */
	public int httpID;
	
	/** ip地址 */
	public String ip="";
	
	/** 登录数据 */
	public ClientLoginData data;
	
	public ClientLoginTransferServerRequest()
	{
		_dataID=ServerMessageType.ClientLoginTransfer;
	}
	
	@Override
	protected void copyData()
	{
		super.copyData();
		ClientLoginData dataTemp=data;
		if(dataTemp!=null)
		{
			this.data=(ClientLoginData)dataTemp.clone();
		}
		else
		{
			this.data=null;
			nullObjError("data");
		}
		
	}
	
	/** 获取数据类名 */
	@Override
	public String getDataClassName()
	{
		return "ClientLoginTransferServerRequest";
	}
	
	/** 写入字节流(简版) */
	@Override
	protected void toWriteBytesSimple(BytesWriteStream stream)
	{
		super.toWriteBytesSimple(stream);
		
		if(this.data!=null)
		{
			stream.writeDataSimpleNotNull(this.data);
		}
		else
		{
			nullObjError("data");
		}
		
		stream.writeInt(this.httpID);
		
		stream.writeUTF(this.ip);
		
	}
	
	/** 转文本输出 */
	@Override
	protected void toWriteDataString(DataWriter writer)
	{
		super.toWriteDataString(writer);
		
		writer.writeTabs();
		writer.sb.append("data");
		writer.sb.append(':');
		if(this.data!=null)
		{
			this.data.writeDataString(writer);
		}
		else
		{
			writer.sb.append("ClientLoginData=null");
		}
		
		writer.writeEnter();
		writer.writeTabs();
		writer.sb.append("httpID");
		writer.sb.append(':');
		writer.sb.append(this.httpID);
		
		writer.writeEnter();
		writer.writeTabs();
		writer.sb.append("ip");
		writer.sb.append(':');
		writer.sb.append(this.ip);
		
		writer.writeEnter();
	}
	
	/** 回池 */
	@Override
	protected void toRelease(DataPool pool)
	{
		super.toRelease(pool);
		
		this.data.release(pool);
		this.data=null;
		this.httpID=0;
		this.ip="";
	}
	
	/** 创建实例 */
	public static ClientLoginTransferServerRequest create(ClientLoginData data,int httpID,String ip)
	{
		ClientLoginTransferServerRequest re=(ClientLoginTransferServerRequest)BytesControl.createRequest(dataID);
		re.data=data;
		re.httpID=httpID;
		re.ip=ip;
		return re;
	}
	
}
