package com.home.commonLogin.net.serverRequest.login.login;
import com.home.commonBase.constlist.generate.ServerMessageType;
import com.home.commonLogin.net.serverRequest.login.base.LoginToLoginServerRequest;
import com.home.shine.bytes.BytesWriteStream;
import com.home.shine.control.BytesControl;
import com.home.shine.support.DataWriter;
import com.home.shine.support.pool.DataPool;

/** 回复客户端登陆错误码(generated by shine) */
public class ReClientLoginErrorServerRequest extends LoginToLoginServerRequest
{
	/** 数据类型ID */
	public static final int dataID=ServerMessageType.ReClientLoginError;
	
	public int httpID;
	
	public int code;
	
	public ReClientLoginErrorServerRequest()
	{
		_dataID=ServerMessageType.ReClientLoginError;
	}
	
	@Override
	protected void copyData()
	{
		super.copyData();
	}
	
	/** 获取数据类名 */
	@Override
	public String getDataClassName()
	{
		return "ReClientLoginErrorServerRequest";
	}
	
	/** 写入字节流(简版) */
	@Override
	protected void toWriteBytesSimple(BytesWriteStream stream)
	{
		super.toWriteBytesSimple(stream);
		
		stream.writeInt(this.httpID);
		
		stream.writeInt(this.code);
		
	}
	
	/** 转文本输出 */
	@Override
	protected void toWriteDataString(DataWriter writer)
	{
		super.toWriteDataString(writer);
		
		writer.writeTabs();
		writer.sb.append("httpID");
		writer.sb.append(':');
		writer.sb.append(this.httpID);
		
		writer.writeEnter();
		writer.writeTabs();
		writer.sb.append("code");
		writer.sb.append(':');
		writer.sb.append(this.code);
		
		writer.writeEnter();
	}
	
	/** 回池 */
	@Override
	protected void toRelease(DataPool pool)
	{
		super.toRelease(pool);
		
		this.httpID=0;
		this.code=0;
	}
	
	/** 创建实例 */
	public static ReClientLoginErrorServerRequest create(int httpID,int code)
	{
		ReClientLoginErrorServerRequest re=(ReClientLoginErrorServerRequest)BytesControl.createRequest(dataID);
		re.httpID=httpID;
		re.code=code;
		return re;
	}
	
}
