package com.home.commonLogin.net.serverResponse.game.login;
import com.home.commonBase.constlist.generate.ServerMessageType;
import com.home.commonLogin.global.LoginC;
import com.home.commonLogin.net.serverResponse.game.base.GameToLoginServerResponse;
import com.home.shine.bytes.BytesReadStream;
import com.home.shine.support.DataWriter;
import com.home.shine.support.pool.DataPool;

/** 刷新Game登录限制到登录服(generated by shine) */
public class RefreshGameLoginLimitToLoginServerResponse extends GameToLoginServerResponse
{
	/** 数据类型ID */
	public static final int dataID=ServerMessageType.RefreshGameLoginLimitToLogin;
	
	public boolean isLoginLimit;
	
	public RefreshGameLoginLimitToLoginServerResponse()
	{
		_dataID=ServerMessageType.RefreshGameLoginLimitToLogin;
	}
	
	/** 获取数据类名 */
	@Override
	public String getDataClassName()
	{
		return "RefreshGameLoginLimitToLoginServerResponse";
	}
	
	/** 读取字节流(简版) */
	@Override
	protected void toReadBytesSimple(BytesReadStream stream)
	{
		super.toReadBytesSimple(stream);
		
		this.isLoginLimit=stream.readBoolean();
		
	}
	
	/** 转文本输出 */
	@Override
	protected void toWriteDataString(DataWriter writer)
	{
		super.toWriteDataString(writer);
		
		writer.writeTabs();
		writer.sb.append("isLoginLimit");
		writer.sb.append(':');
		writer.sb.append(this.isLoginLimit);
		
		writer.writeEnter();
	}
	
	/** 回池 */
	@Override
	protected void toRelease(DataPool pool)
	{
		super.toRelease(pool);
		
		this.isLoginLimit=false;
	}
	
	/** 执行 */
	@Override
	protected void execute()
	{
		LoginC.main.onRefreshGameLoginLimit(fromGameID,isLoginLimit);
	}
	
}
