package com.home.commonLogin.net.serverResponse.game.system;
import com.home.commonBase.constlist.generate.ServerMessageType;
import com.home.commonLogin.global.LoginC;
import com.home.commonLogin.net.serverResponse.game.base.GameToLoginServerResponse;
import com.home.shine.bytes.BytesReadStream;
import com.home.shine.support.DataWriter;
import com.home.shine.support.pool.DataPool;

/** 限制某取服注册到登陆服(generated by shine) */
public class LimitAreaToLoginServerResponse extends GameToLoginServerResponse
{
	/** 数据类型ID */
	public static final int dataID=ServerMessageType.LimitAreaToLogin;
	
	/** 是否限制注册 */
	public boolean isLimitRegist;
	
	/** 区服ID */
	public int areaID;
	
	public LimitAreaToLoginServerResponse()
	{
		_dataID=ServerMessageType.LimitAreaToLogin;
	}
	
	/** 获取数据类名 */
	@Override
	public String getDataClassName()
	{
		return "LimitAreaToLoginServerResponse";
	}
	
	/** 读取字节流(简版) */
	@Override
	protected void toReadBytesSimple(BytesReadStream stream)
	{
		super.toReadBytesSimple(stream);
		
		this.areaID=stream.readInt();
		
		this.isLimitRegist=stream.readBoolean();
		
	}
	
	/** 转文本输出 */
	@Override
	protected void toWriteDataString(DataWriter writer)
	{
		super.toWriteDataString(writer);
		
		writer.writeTabs();
		writer.sb.append("areaID");
		writer.sb.append(':');
		writer.sb.append(this.areaID);
		
		writer.writeEnter();
		writer.writeTabs();
		writer.sb.append("isLimitRegist");
		writer.sb.append(':');
		writer.sb.append(this.isLimitRegist);
		
		writer.writeEnter();
	}
	
	/** 执行 */
	@Override
	protected void execute()
	{
		LoginC.main.limitArea(areaID,isLimitRegist);
	}
	
	/** 回池 */
	@Override
	protected void toRelease(DataPool pool)
	{
		super.toRelease(pool);
		
		this.areaID=0;
		this.isLimitRegist=false;
	}
	
}
