package com.home.commonLogin.net.serverResponse.manager;
import com.home.commonBase.constlist.generate.ServerMessageType;
import com.home.commonLogin.net.serverResponse.manager.base.ManagerToLoginServerResponse;
import com.home.shine.ShineSetup;
import com.home.shine.bytes.BytesReadStream;
import com.home.shine.control.ThreadControl;
import com.home.shine.ctrl.Ctrl;

/** 关闭登录服消息(generated by shine) */
public class LoginExitServerResponse extends ManagerToLoginServerResponse
{
	/** 数据类型ID */
	public static final int dataID=ServerMessageType.LoginExit;
	
	public LoginExitServerResponse()
	{
		_dataID=ServerMessageType.LoginExit;
	}
	
	/** 获取数据类名 */
	@Override
	public String getDataClassName()
	{
		return "LoginExitServerResponse";
	}
	
	/** 执行 */
	@Override
	protected void execute()
	{
		Ctrl.log("收到登录服关闭消息(来自管理服)");
		
		ThreadControl.getMainTimeDriver().callLater(ShineSetup::exit);
	}
	
}
