package com.home.commonLogin.net.serverResponse.manager.system;
import com.home.commonBase.constlist.generate.ServerMessageType;
import com.home.commonLogin.global.LoginC;
import com.home.commonLogin.net.serverResponse.manager.base.ManagerToLoginServerResponse;
import com.home.shine.bytes.BytesReadStream;
import com.home.shine.support.DataWriter;
import com.home.shine.support.pool.DataPool;

/** 推送服务器开放状态到登陆服(generated by shine) */
public class SendServerOpenToLoginServerResponse extends ManagerToLoginServerResponse
{
	/** 数据类型ID */
	public static final int dataID=ServerMessageType.SendServerOpenToLogin;
	
	/** 当前开放状态 */
	public boolean isOpen;
	
	public SendServerOpenToLoginServerResponse()
	{
		_dataID=ServerMessageType.SendServerOpenToLogin;
	}
	
	/** 获取数据类名 */
	@Override
	public String getDataClassName()
	{
		return "SendServerOpenToLoginServerResponse";
	}
	
	/** 读取字节流(简版) */
	@Override
	protected void toReadBytesSimple(BytesReadStream stream)
	{
		super.toReadBytesSimple(stream);
		
		this.isOpen=stream.readBoolean();
		
	}
	
	/** 转文本输出 */
	@Override
	protected void toWriteDataString(DataWriter writer)
	{
		super.toWriteDataString(writer);
		
		writer.writeTabs();
		writer.sb.append("isOpen");
		writer.sb.append(':');
		writer.sb.append(this.isOpen);
		
		writer.writeEnter();
	}
	
	/** 执行 */
	@Override
	protected void execute()
	{
		LoginC.main.setIsOpen(isOpen);
	}
	
	/** 回池 */
	@Override
	protected void toRelease(DataPool pool)
	{
		super.toRelease(pool);
		
		this.isOpen=false;
	}
	
}
