package com.home.commonManager.net.httpResponse.role;
import com.home.commonBase.data.system.PlayerWorkData;
import com.home.commonManager.constlist.generate.ManagerHttpResponseType;
import com.home.commonManager.net.httpResponse.base.ManagerRHttpResponse;
import com.home.commonManager.net.httpResponseResult.role.MAddPlayerWorkResult;
import com.home.commonManager.net.serverRequest.center.MAddPlayerWorkToCenterServerRequest;
import com.home.shine.bytes.BytesReadStream;

/** gm添加事务消息(generated by shine) */
public class MAddPlayerWorkHttpResponse extends ManagerRHttpResponse
{
	/** 数据类型ID */
	public static final int dataID=ManagerHttpResponseType.MAddPlayerWork;
	
	/** 返回值 */
	protected MAddPlayerWorkResult _re=new MAddPlayerWorkResult();
	
	/** 角色事务 */
	public PlayerWorkData work;
	
	public MAddPlayerWorkHttpResponse()
	{
		_dataID=ManagerHttpResponseType.MAddPlayerWork;
	}
	
	/** 执行 */
	@Override
	protected void execute()
	{
		MAddPlayerWorkToCenterServerRequest.create(httpInstanceID,work).send();
	}
	
	/** 读取字节流(简版) */
	@Override
	protected void toReadBytesSimple(BytesReadStream stream)
	{
		super.toReadBytesSimple(stream);
		
		this.work=(PlayerWorkData)stream.readDataSimpleNotNull();
		
	}
	
	/** 收到返回方法 */
	public void reBack(boolean success)
	{
		_re.success=success;
		resultData(_re);
	}
	
}
