package com.home.commonManager.net.httpResponse.system;
import com.home.commonManager.constlist.generate.ManagerHttpResponseType;
import com.home.commonManager.dataEx.GMClientUser;
import com.home.commonManager.global.ManagerC;
import com.home.commonManager.net.base.ManagerHttpResponse;
import com.home.commonManager.net.httpResponseResult.system.GMClientLoginResult;
import com.home.shine.bytes.BytesReadStream;

/** gm客户端登陆消息(generated by shine) */
public class GMClientLoginHttpResponse extends ManagerHttpResponse
{
	/** 数据类型ID */
	public static final int dataID=ManagerHttpResponseType.GMClientLogin;
	
	/** 返回值 */
	protected GMClientLoginResult _re=new GMClientLoginResult();
	
	/** user */
	public String user="";
	
	/** 密码 */
	public String password="";
	
	public GMClientLoginHttpResponse()
	{
		_dataID=ManagerHttpResponseType.GMClientLogin;
	}
	
	/** 执行 */
	@Override
	protected void execute()
	{
		GMClientUser obj=ManagerC.main.userLogin(user,password);
		
		_re.token=obj.token;
		
		resultData(_re);
	}
	
	/** 读取字节流(简版) */
	@Override
	protected void toReadBytesSimple(BytesReadStream stream)
	{
		super.toReadBytesSimple(stream);
		
		this.user=stream.readUTF();
		
		this.password=stream.readUTF();
		
	}
	
	/** 收到返回方法 */
	public void reBack(int token)
	{
		_re.token=token;
		resultData(_re);
	}
	
}
