package com.home.commonManager.net.serverRequest.center;
import com.home.commonBase.constlist.generate.ServerMessageType;
import com.home.commonBase.data.system.PlayerWorkData;
import com.home.commonManager.net.base.ManagerServerRequest;
import com.home.commonManager.net.serverRequest.center.base.ManagerToCenterServerRequest;
import com.home.shine.bytes.BytesWriteStream;
import com.home.shine.control.BytesControl;
import com.home.shine.support.DataWriter;
import com.home.shine.support.pool.DataPool;

/** 执行查询事务到中心服(generated by shine) */
public class MQueryPlayerWorkToCenterServerRequest extends ManagerToCenterServerRequest
{
	/** 数据类型ID */
	public static final int dataID=ServerMessageType.MQueryPlayerWorkToCenter;
	
	public int httpID;
	
	/** 数据 */
	public PlayerWorkData data;
	
	public MQueryPlayerWorkToCenterServerRequest()
	{
		_dataID=ServerMessageType.MQueryPlayerWorkToCenter;
	}
	
	@Override
	protected void copyData()
	{
		super.copyData();
		PlayerWorkData dataTemp=data;
		if(dataTemp!=null)
		{
			this.data=(PlayerWorkData)dataTemp.clone();
		}
		else
		{
			this.data=null;
			nullObjError("data");
		}
		
	}
	
	/** 获取数据类名 */
	@Override
	public String getDataClassName()
	{
		return "MQueryPlayerWorkToCenterServerRequest";
	}
	
	/** 写入字节流(简版) */
	@Override
	protected void toWriteBytesSimple(BytesWriteStream stream)
	{
		super.toWriteBytesSimple(stream);
		
		stream.writeInt(this.httpID);
		
		if(this.data!=null)
		{
			stream.writeDataSimpleNotNull(this.data);
		}
		else
		{
			nullObjError("data");
		}
		
	}
	
	/** 转文本输出 */
	@Override
	protected void toWriteDataString(DataWriter writer)
	{
		super.toWriteDataString(writer);
		
		writer.writeTabs();
		writer.sb.append("httpID");
		writer.sb.append(':');
		writer.sb.append(this.httpID);
		
		writer.writeEnter();
		writer.writeTabs();
		writer.sb.append("data");
		writer.sb.append(':');
		if(this.data!=null)
		{
			this.data.writeDataString(writer);
		}
		else
		{
			writer.sb.append("PlayerWorkData=null");
		}
		
		writer.writeEnter();
	}
	
	/** 回池 */
	@Override
	protected void toRelease(DataPool pool)
	{
		super.toRelease(pool);
		
		this.httpID=0;
		this.data.release(pool);
		this.data=null;
	}
	
	/** 创建实例 */
	public static MQueryPlayerWorkToCenterServerRequest create(int httpID,PlayerWorkData data)
	{
		MQueryPlayerWorkToCenterServerRequest re=(MQueryPlayerWorkToCenterServerRequest)BytesControl.createRequest(dataID);
		re.httpID=httpID;
		re.data=data;
		return re;
	}
	
}
