package com.home.commonScene.net.sceneResponse.login;
import com.home.commonScene.constlist.generate.SceneResponseType;
import com.home.commonScene.global.SceneC;
import com.home.commonScene.net.sceneResponse.base.SceneLoginResponse;
import com.home.commonScene.server.SceneReceiveSocket;
import com.home.shine.bytes.BytesReadStream;
import com.home.shine.support.DataWriter;
import com.home.shine.support.pool.DataPool;

/** 角色切换场景服消息(generated by shine) */
public class PlayerSwitchSceneResponse extends SceneLoginResponse
{
	/** 数据类型ID */
	public static final int dataID=SceneResponseType.PlayerSwitchScene;
	
	/** 令牌 */
	public int token;
	
	public PlayerSwitchSceneResponse()
	{
		_dataID=SceneResponseType.PlayerSwitchScene;
	}
	
	/** 获取数据类名 */
	@Override
	public String getDataClassName()
	{
		return "PlayerSwitchSceneResponse";
	}
	
	/** 读取字节流(完整版) */
	@Override
	protected void toReadBytesFull(BytesReadStream stream)
	{
		super.toReadBytesFull(stream);
		
		stream.startReadObj();
		
		this.token=stream.readInt();
		
		stream.endReadObj();
	}
	
	/** 读取字节流(简版) */
	@Override
	protected void toReadBytesSimple(BytesReadStream stream)
	{
		super.toReadBytesSimple(stream);
		
		this.token=stream.readInt();
		
	}
	
	/** 转文本输出 */
	@Override
	protected void toWriteDataString(DataWriter writer)
	{
		super.toWriteDataString(writer);
		
		writer.writeTabs();
		writer.sb.append("token");
		writer.sb.append(':');
		writer.sb.append(this.token);
		
		writer.writeEnter();
	}
	
	/** 回池 */
	@Override
	protected void toRelease(DataPool pool)
	{
		super.toRelease(pool);
		
		this.token=0;
	}
	
	/** 执行 */
	@Override
	protected void execute()
	{
		SceneC.main.playerSwitchSceneReceive((SceneReceiveSocket)socket,token);
	}
	
}
