package com.home.commonScene.net.serverResponse.manager;
import com.home.commonBase.constlist.generate.ServerMessageType;
import com.home.commonScene.net.serverResponse.manager.base.ManagerToSceneServerResponse;
import com.home.shine.ShineSetup;
import com.home.shine.bytes.BytesReadStream;
import com.home.shine.control.ThreadControl;
import com.home.shine.ctrl.Ctrl;

/** 场景服退出消息(generated by shine) */
public class SceneExitServerResponse extends ManagerToSceneServerResponse
{
	/** 数据类型ID */
	public static final int dataID=ServerMessageType.SceneExit;
	
	public SceneExitServerResponse()
	{
		_dataID=ServerMessageType.SceneExit;
	}
	
	/** 获取数据类名 */
	@Override
	public String getDataClassName()
	{
		return "SceneExitServerResponse";
	}
	
	/** 执行 */
	@Override
	protected void execute()
	{
		Ctrl.log("收到场景服关闭消息(来自管理服)");
		
		ThreadControl.getMainTimeDriver().callLater(ShineSetup::exit);
	}
	
}
