package com.home.commonSceneBase.net.sceneBaseRequest.scene;
import com.home.commonBase.data.scene.scene.FieldItemBagBindData;
import com.home.commonSceneBase.constlist.generate.SceneBaseRequestType;
import com.home.commonSceneBase.net.sceneBaseRequest.base.SceneSRequest;
import com.home.shine.bytes.BytesWriteStream;
import com.home.shine.control.BytesControl;
import com.home.shine.support.DataWriter;
import com.home.shine.support.pool.DataPool;

/** 添加掉落物品包绑定消息(generated by shine) */
public class AddFieldItemBagBindRequest extends SceneSRequest
{
	/** 数据类型ID */
	public static final int dataID=SceneBaseRequestType.AddFieldItemBagBind;
	
	/** 掉落物品包绑定消息 */
	public FieldItemBagBindData data;
	
	public AddFieldItemBagBindRequest()
	{
		_dataID=SceneBaseRequestType.AddFieldItemBagBind;
	}
	
	@Override
	protected void copyData()
	{
		super.copyData();
		FieldItemBagBindData dataTemp=data;
		if(dataTemp!=null)
		{
			this.data=(FieldItemBagBindData)dataTemp.clone();
		}
		else
		{
			this.data=null;
			nullObjError("data");
		}
		
	}
	
	/** 获取数据类名 */
	@Override
	public String getDataClassName()
	{
		return "AddFieldItemBagBindRequest";
	}
	
	/** 写入字节流(完整版) */
	@Override
	protected void toWriteBytesFull(BytesWriteStream stream)
	{
		super.toWriteBytesFull(stream);
		
		stream.startWriteObj();
		
		if(this.data!=null)
		{
			stream.writeDataFullNotNull(this.data);
		}
		else
		{
			nullObjError("data");
		}
		
		stream.endWriteObj();
	}
	
	/** 写入字节流(简版) */
	@Override
	protected void toWriteBytesSimple(BytesWriteStream stream)
	{
		super.toWriteBytesSimple(stream);
		
		if(this.data!=null)
		{
			stream.writeDataSimpleNotNull(this.data);
		}
		else
		{
			nullObjError("data");
		}
		
	}
	
	/** 转文本输出 */
	@Override
	protected void toWriteDataString(DataWriter writer)
	{
		super.toWriteDataString(writer);
		
		writer.writeTabs();
		writer.sb.append("data");
		writer.sb.append(':');
		if(this.data!=null)
		{
			this.data.writeDataString(writer);
		}
		else
		{
			writer.sb.append("FieldItemBagBindData=null");
		}
		
		writer.writeEnter();
	}
	
	/** 回池 */
	@Override
	protected void toRelease(DataPool pool)
	{
		super.toRelease(pool);
		
		this.data.release(pool);
		this.data=null;
	}
	
	/** 创建实例 */
	public static AddFieldItemBagBindRequest create(FieldItemBagBindData data)
	{
		AddFieldItemBagBindRequest re=(AddFieldItemBagBindRequest)BytesControl.createRequest(dataID);
		re.data=data;
		return re;
	}
	
}
