package com.home.commonSceneBase.net.sceneBaseRequest.syncScene;
import com.home.commonBase.data.scene.fight.FrameSyncData;
import com.home.commonSceneBase.constlist.generate.SceneBaseRequestType;
import com.home.commonSceneBase.net.sceneBaseRequest.base.SceneSRequest;
import com.home.shine.bytes.BytesWriteStream;
import com.home.shine.control.BytesControl;
import com.home.shine.support.DataWriter;
import com.home.shine.support.pool.DataPool;

/** 帧同步每帧消息(generated by shine) */
public class FrameSyncFrameRequest extends SceneSRequest
{
	/** 数据类型ID */
	public static final int dataID=SceneBaseRequestType.FrameSyncFrame;
	
	/** 帧数据 */
	public FrameSyncData frame;
	
	public FrameSyncFrameRequest()
	{
		_dataID=SceneBaseRequestType.FrameSyncFrame;
		setDontCopy();
	}
	
	@Override
	protected void copyData()
	{
		super.copyData();
		FrameSyncData frameTemp=frame;
		if(frameTemp!=null)
		{
			this.frame=(FrameSyncData)frameTemp.clone();
		}
		else
		{
			this.frame=null;
			nullObjError("frame");
		}
		
	}
	
	/** 获取数据类名 */
	@Override
	public String getDataClassName()
	{
		return "FrameSyncFrameRequest";
	}
	
	/** 转文本输出 */
	@Override
	protected void toWriteDataString(DataWriter writer)
	{
		super.toWriteDataString(writer);
		
		writer.writeTabs();
		writer.sb.append("frame");
		writer.sb.append(':');
		if(this.frame!=null)
		{
			this.frame.writeDataString(writer);
		}
		else
		{
			writer.sb.append("FrameSyncData=null");
		}
		
		writer.writeEnter();
	}
	
	/** 写入字节流(完整版) */
	@Override
	protected void toWriteBytesFull(BytesWriteStream stream)
	{
		super.toWriteBytesFull(stream);
		
		stream.startWriteObj();
		
		if(this.frame!=null)
		{
			stream.writeDataFullNotNull(this.frame);
		}
		else
		{
			nullObjError("frame");
		}
		
		stream.endWriteObj();
	}
	
	/** 写入字节流(简版) */
	@Override
	protected void toWriteBytesSimple(BytesWriteStream stream)
	{
		super.toWriteBytesSimple(stream);
		
		if(this.frame!=null)
		{
			stream.writeDataSimpleNotNull(this.frame);
		}
		else
		{
			nullObjError("frame");
		}
		
	}
	
	/** 回池 */
	@Override
	protected void toRelease(DataPool pool)
	{
		super.toRelease(pool);
		
		this.frame=null;
	}
	
	/** 创建实例 */
	public static FrameSyncFrameRequest create(FrameSyncData frame)
	{
		FrameSyncFrameRequest re=(FrameSyncFrameRequest)BytesControl.createRequest(dataID);
		re.frame=frame;
		return re;
	}
	
}
