package com.home.commonSceneBase.net.sceneBaseRequest.unit;
import com.home.commonBase.data.scene.base.BulletData;
import com.home.commonSceneBase.constlist.generate.SceneBaseRequestType;
import com.home.commonSceneBase.net.sceneBaseRequest.base.UnitSRequest;
import com.home.shine.bytes.BytesWriteStream;
import com.home.shine.control.BytesControl;
import com.home.shine.support.DataWriter;
import com.home.shine.support.pool.DataPool;

/** 单位发出子弹消息(generated by shine) */
public class AddBulletRequest extends UnitSRequest
{
	/** 数据类型ID */
	public static final int dataID=SceneBaseRequestType.AddBullet;
	
	/** 子弹数据 */
	public BulletData bullet;
	
	public AddBulletRequest()
	{
		_dataID=SceneBaseRequestType.AddBullet;
	}
	
	@Override
	protected void copyData()
	{
		super.copyData();
		BulletData bulletTemp=bullet;
		if(bulletTemp!=null)
		{
			this.bullet=(BulletData)bulletTemp.clone();
		}
		else
		{
			this.bullet=null;
			nullObjError("bullet");
		}
		
	}
	
	/** 获取数据类名 */
	@Override
	public String getDataClassName()
	{
		return "AddBulletRequest";
	}
	
	/** 转文本输出 */
	@Override
	protected void toWriteDataString(DataWriter writer)
	{
		super.toWriteDataString(writer);
		
		writer.writeTabs();
		writer.sb.append("bullet");
		writer.sb.append(':');
		if(this.bullet!=null)
		{
			this.bullet.writeDataString(writer);
		}
		else
		{
			writer.sb.append("BulletData=null");
		}
		
		writer.writeEnter();
	}
	
	/** 写入字节流(完整版) */
	@Override
	protected void toWriteBytesFull(BytesWriteStream stream)
	{
		super.toWriteBytesFull(stream);
		
		stream.startWriteObj();
		
		if(this.bullet!=null)
		{
			stream.writeDataFullNotNull(this.bullet);
		}
		else
		{
			nullObjError("bullet");
		}
		
		stream.endWriteObj();
	}
	
	/** 写入字节流(简版) */
	@Override
	protected void toWriteBytesSimple(BytesWriteStream stream)
	{
		super.toWriteBytesSimple(stream);
		
		if(this.bullet!=null)
		{
			stream.writeDataSimpleNotNull(this.bullet);
		}
		else
		{
			nullObjError("bullet");
		}
		
	}
	
	/** 回池 */
	@Override
	protected void toRelease(DataPool pool)
	{
		super.toRelease(pool);
		
		this.bullet.release(pool);
		this.bullet=null;
	}
	
	/** 创建实例 */
	public static AddBulletRequest create(int instanceID,BulletData bullet)
	{
		AddBulletRequest re=(AddBulletRequest)BytesControl.createRequest(dataID);
		re.instanceID=instanceID;
		re.bullet=bullet;
		return re;
	}
	
}
