package com.home.commonSceneBase.net.sceneBaseRequest.unit;
import com.home.commonSceneBase.constlist.generate.SceneBaseRequestType;
import com.home.commonSceneBase.net.sceneBaseRequest.unit.ReCUnitSkillFailedRequest;
import com.home.shine.bytes.BytesWriteStream;
import com.home.shine.control.BytesControl;
import com.home.shine.support.DataWriter;
import com.home.shine.support.pool.DataPool;

/** 回复技能失败消息(generated by shine) */
public class ReCUnitSkillFailedExRequest extends ReCUnitSkillFailedRequest
{
	/** 数据类型ID */
	public static final int dataID=SceneBaseRequestType.ReCUnitSkillFailedEx;
	
	/** 当前的种子序号 */
	public int seedIndex;
	
	public ReCUnitSkillFailedExRequest()
	{
		_dataID=SceneBaseRequestType.ReCUnitSkillFailedEx;
	}
	
	@Override
	protected void copyData()
	{
		super.copyData();
	}
	
	/** 获取数据类名 */
	@Override
	public String getDataClassName()
	{
		return "ReCUnitSkillFailedExRequest";
	}
	
	/** 转文本输出 */
	@Override
	protected void toWriteDataString(DataWriter writer)
	{
		super.toWriteDataString(writer);
		
		writer.writeTabs();
		writer.sb.append("seedIndex");
		writer.sb.append(':');
		writer.sb.append(this.seedIndex);
		
		writer.writeEnter();
	}
	
	/** 写入字节流(完整版) */
	@Override
	protected void toWriteBytesFull(BytesWriteStream stream)
	{
		super.toWriteBytesFull(stream);
		
		stream.startWriteObj();
		
		stream.writeInt(this.seedIndex);
		
		stream.endWriteObj();
	}
	
	/** 写入字节流(简版) */
	@Override
	protected void toWriteBytesSimple(BytesWriteStream stream)
	{
		super.toWriteBytesSimple(stream);
		
		stream.writeInt(this.seedIndex);
		
	}
	
	/** 回池 */
	@Override
	protected void toRelease(DataPool pool)
	{
		super.toRelease(pool);
		
		this.seedIndex=0;
	}
	
	/** 创建实例 */
	public static ReCUnitSkillFailedExRequest create(int instanceID,int skillID,int seedIndex)
	{
		ReCUnitSkillFailedExRequest re=(ReCUnitSkillFailedExRequest)BytesControl.createRequest(dataID);
		re.instanceID=instanceID;
		re.skillID=skillID;
		re.seedIndex=seedIndex;
		return re;
	}
	
}
