package com.home.commonSceneBase.net.sceneBaseRequest.unit;
import com.home.commonSceneBase.constlist.generate.SceneBaseRequestType;
import com.home.commonSceneBase.net.sceneBaseRequest.base.UnitSRequest;
import com.home.shine.bytes.BytesWriteStream;
import com.home.shine.control.BytesControl;
import com.home.shine.support.DataWriter;
import com.home.shine.support.pool.DataPool;

/** 单位死亡消息(generated by shine) */
public class UnitDeadRequest extends UnitSRequest
{
	/** 数据类型ID */
	public static final int dataID=SceneBaseRequestType.UnitDead;
	
	/** 死亡类型 */
	public int type;
	
	/** 是否真死亡(否则就是死后复活) */
	public boolean isReal;
	
	/** 击杀者实例ID */
	public int attackerInstanceID;
	
	public UnitDeadRequest()
	{
		_dataID=SceneBaseRequestType.UnitDead;
	}
	
	@Override
	protected void copyData()
	{
		super.copyData();
	}
	
	/** 获取数据类名 */
	@Override
	public String getDataClassName()
	{
		return "UnitDeadRequest";
	}
	
	/** 转文本输出 */
	@Override
	protected void toWriteDataString(DataWriter writer)
	{
		super.toWriteDataString(writer);
		
		writer.writeTabs();
		writer.sb.append("attackerInstanceID");
		writer.sb.append(':');
		writer.sb.append(this.attackerInstanceID);
		
		writer.writeEnter();
		writer.writeTabs();
		writer.sb.append("type");
		writer.sb.append(':');
		writer.sb.append(this.type);
		
		writer.writeEnter();
		writer.writeTabs();
		writer.sb.append("isReal");
		writer.sb.append(':');
		writer.sb.append(this.isReal);
		
		writer.writeEnter();
	}
	
	/** 写入字节流(完整版) */
	@Override
	protected void toWriteBytesFull(BytesWriteStream stream)
	{
		super.toWriteBytesFull(stream);
		
		stream.startWriteObj();
		
		stream.writeInt(this.attackerInstanceID);
		
		stream.writeInt(this.type);
		
		stream.writeBoolean(this.isReal);
		
		stream.endWriteObj();
	}
	
	/** 写入字节流(简版) */
	@Override
	protected void toWriteBytesSimple(BytesWriteStream stream)
	{
		super.toWriteBytesSimple(stream);
		
		stream.writeInt(this.attackerInstanceID);
		
		stream.writeInt(this.type);
		
		stream.writeBoolean(this.isReal);
		
	}
	
	/** 回池 */
	@Override
	protected void toRelease(DataPool pool)
	{
		super.toRelease(pool);
		
		this.attackerInstanceID=0;
		this.type=0;
		this.isReal=false;
	}
	
	/** 创建实例 */
	public static UnitDeadRequest create(int instanceID,int attackerInstanceID,int type,boolean isReal)
	{
		UnitDeadRequest re=(UnitDeadRequest)BytesControl.createRequest(dataID);
		re.instanceID=instanceID;
		re.attackerInstanceID=attackerInstanceID;
		re.type=type;
		re.isReal=isReal;
		return re;
	}
	
}
