package com.home.commonSceneBase.net.sceneBaseRequest.unit;
import com.home.commonSceneBase.constlist.generate.SceneBaseRequestType;
import com.home.commonSceneBase.net.sceneBaseRequest.base.UnitSRequest;
import com.home.shine.bytes.BytesWriteStream;
import com.home.shine.control.BytesControl;
import com.home.shine.support.DataWriter;
import com.home.shine.support.pool.DataPool;

/** 单位上载具消息(generated by shine) */
public class UnitGetOnVehicleRequest extends UnitSRequest
{
	/** 数据类型ID */
	public static final int dataID=SceneBaseRequestType.UnitGetOnVehicle;
	
	public int vehicleInstanceID;
	
	public int index;
	
	public UnitGetOnVehicleRequest()
	{
		_dataID=SceneBaseRequestType.UnitGetOnVehicle;
	}
	
	@Override
	protected void copyData()
	{
		super.copyData();
	}
	
	/** 获取数据类名 */
	@Override
	public String getDataClassName()
	{
		return "UnitGetOnVehicleRequest";
	}
	
	/** 写入字节流(完整版) */
	@Override
	protected void toWriteBytesFull(BytesWriteStream stream)
	{
		super.toWriteBytesFull(stream);
		
		stream.startWriteObj();
		
		stream.writeInt(this.vehicleInstanceID);
		
		stream.writeInt(this.index);
		
		stream.endWriteObj();
	}
	
	/** 写入字节流(简版) */
	@Override
	protected void toWriteBytesSimple(BytesWriteStream stream)
	{
		super.toWriteBytesSimple(stream);
		
		stream.writeInt(this.vehicleInstanceID);
		
		stream.writeInt(this.index);
		
	}
	
	/** 转文本输出 */
	@Override
	protected void toWriteDataString(DataWriter writer)
	{
		super.toWriteDataString(writer);
		
		writer.writeTabs();
		writer.sb.append("vehicleInstanceID");
		writer.sb.append(':');
		writer.sb.append(this.vehicleInstanceID);
		
		writer.writeEnter();
		writer.writeTabs();
		writer.sb.append("index");
		writer.sb.append(':');
		writer.sb.append(this.index);
		
		writer.writeEnter();
	}
	
	/** 回池 */
	@Override
	protected void toRelease(DataPool pool)
	{
		super.toRelease(pool);
		
		this.vehicleInstanceID=0;
		this.index=0;
	}
	
	/** 创建实例 */
	public static UnitGetOnVehicleRequest create(int instanceID,int vehicleInstanceID,int index)
	{
		UnitGetOnVehicleRequest re=(UnitGetOnVehicleRequest)BytesControl.createRequest(dataID);
		re.instanceID=instanceID;
		re.vehicleInstanceID=vehicleInstanceID;
		re.index=index;
		return re;
	}
	
}
