package com.home.commonSceneBase.net.sceneBaseRequest.unit.building;
import com.home.commonSceneBase.constlist.generate.SceneBaseRequestType;
import com.home.commonSceneBase.net.sceneBaseRequest.base.UnitSRequest;
import com.home.shine.bytes.BytesWriteStream;
import com.home.shine.control.BytesControl;
import com.home.shine.support.DataWriter;
import com.home.shine.support.pool.DataPool;

/** 建筑升级成功的广播协议 注意:包含父类参数 instanceID(generated by shine) */
public class BuildingLevelUpingCompleteRequest extends UnitSRequest
{
	/** 数据类型ID */
	public static final int dataID=SceneBaseRequestType.BuildingLevelUpingComplete;
	
	/** 下一等级 */
	public int level;
	
	public BuildingLevelUpingCompleteRequest()
	{
		_dataID=SceneBaseRequestType.BuildingLevelUpingComplete;
	}
	
	@Override
	protected void copyData()
	{
		super.copyData();
	}
	
	/** 获取数据类名 */
	@Override
	public String getDataClassName()
	{
		return "BuildingLevelUpingCompleteRequest";
	}
	
	/** 写入字节流(完整版) */
	@Override
	protected void toWriteBytesFull(BytesWriteStream stream)
	{
		super.toWriteBytesFull(stream);
		
		stream.startWriteObj();
		
		stream.writeInt(this.level);
		
		stream.endWriteObj();
	}
	
	/** 写入字节流(简版) */
	@Override
	protected void toWriteBytesSimple(BytesWriteStream stream)
	{
		super.toWriteBytesSimple(stream);
		
		stream.writeInt(this.level);
		
	}
	
	/** 转文本输出 */
	@Override
	protected void toWriteDataString(DataWriter writer)
	{
		super.toWriteDataString(writer);
		
		writer.writeTabs();
		writer.sb.append("level");
		writer.sb.append(':');
		writer.sb.append(this.level);
		
		writer.writeEnter();
	}
	
	/** 回池 */
	@Override
	protected void toRelease(DataPool pool)
	{
		super.toRelease(pool);
		
		this.level=0;
	}
	
	/** 创建实例 */
	public static BuildingLevelUpingCompleteRequest create(int instanceID,int level)
	{
		BuildingLevelUpingCompleteRequest re=(BuildingLevelUpingCompleteRequest)BytesControl.createRequest(dataID);
		re.instanceID=instanceID;
		re.level=level;
		return re;
	}
	
}
