package com.home.commonSceneBase.net.sceneBaseResponse.unit;
import com.home.commonBase.data.social.chat.ChatData;
import com.home.commonBase.utils.BaseGameUtils;
import com.home.commonSceneBase.constlist.generate.SceneBaseResponseType;
import com.home.commonSceneBase.net.sceneBaseRequest.unit.UnitChatRequest;
import com.home.commonSceneBase.net.sceneBaseResponse.base.CUnitRResponse;
import com.home.shine.bytes.BytesReadStream;
import com.home.shine.data.BaseData;
import com.home.shine.support.DataWriter;
import com.home.shine.support.pool.DataPool;

/** 单位聊天消息(generated by shine) */
public class CUnitChatResponse extends CUnitRResponse
{
	/** 数据类型ID */
	public static final int dataID=SceneBaseResponseType.CUnitChat;
	
	/** 聊天数据 */
	public ChatData data;
	
	public CUnitChatResponse()
	{
		_dataID=SceneBaseResponseType.CUnitChat;
	}
	
	/** 执行 */
	@Override
	protected void execute()
	{
		//替换敏感字
		data.text=BaseGameUtils.replaceSensitiveWord(data.text);
		//广播消息
		unit.radioMessage(UnitChatRequest.create(instanceID,data),true);
	}
	
	/** 获取数据类名 */
	@Override
	public String getDataClassName()
	{
		return "CUnitChatResponse";
	}
	
	/** 读取字节流(完整版) */
	@Override
	protected void toReadBytesFull(BytesReadStream stream)
	{
		super.toReadBytesFull(stream);
		
		stream.startReadObj();
		
		BaseData dataT=stream.readDataFullNotNull();
		if(dataT!=null)
		{
			if(dataT instanceof ChatData)
			{
				this.data=(ChatData)dataT;
			}
			else
			{
				this.data=new ChatData();
				if(!(dataT.getClass().isAssignableFrom(ChatData.class)))
				{
					stream.throwTypeReadError(ChatData.class,dataT.getClass());
				}
				this.data.shadowCopy(dataT);
			}
		}
		else
		{
			this.data=null;
		}
		
		stream.endReadObj();
	}
	
	/** 读取字节流(简版) */
	@Override
	protected void toReadBytesSimple(BytesReadStream stream)
	{
		super.toReadBytesSimple(stream);
		
		this.data=(ChatData)stream.readDataSimpleNotNull();
		
	}
	
	/** 转文本输出 */
	@Override
	protected void toWriteDataString(DataWriter writer)
	{
		super.toWriteDataString(writer);
		
		writer.writeTabs();
		writer.sb.append("data");
		writer.sb.append(':');
		if(this.data!=null)
		{
			this.data.writeDataString(writer);
		}
		else
		{
			writer.sb.append("ChatData=null");
		}
		
		writer.writeEnter();
	}
	
	/** 回池 */
	@Override
	protected void toRelease(DataPool pool)
	{
		super.toRelease(pool);
		
		this.data=null;
	}
	
}
