package com.home.commonSceneBase.net.sceneBaseResponse.unit;
import com.home.commonBase.data.scene.base.DirData;
import com.home.commonBase.data.scene.base.PosData;
import com.home.commonSceneBase.constlist.generate.SceneBaseResponseType;
import com.home.commonSceneBase.net.sceneBaseResponse.base.CUnitRResponse;
import com.home.shine.bytes.BytesReadStream;
import com.home.shine.support.DataWriter;
import com.home.shine.support.pool.DataPool;

/** 客户端单位朝向移动消息(generated by shine) */
public class CUnitMoveDirResponse extends CUnitRResponse
{
	/** 数据类型ID */
	public static final int dataID=SceneBaseResponseType.CUnitMoveDir;
	
	/** 移动类型 */
	public int type;
	
	/** 当前位置 */
	public PosData pos;
	
	/** 实际移动方向 */
	public DirData realDir;
	
	/** 实际速度比率 */
	public int realSpeedRatio=-1;
	
	/** 目标朝向 */
	public DirData dir;
	
	public CUnitMoveDirResponse()
	{
		_dataID=SceneBaseResponseType.CUnitMoveDir;
	}
	
	/** 执行 */
	protected void execute()
	{
		unit.move.onClientMoveDir(pos,type,dir,realDir,realSpeedRatio);
	}
	
	/** 获取数据类名 */
	@Override
	public String getDataClassName()
	{
		return "CUnitMoveDirResponse";
	}
	
	/** 读取字节流(完整版) */
	@Override
	protected void toReadBytesFull(BytesReadStream stream)
	{
		super.toReadBytesFull(stream);
		
		stream.startReadObj();
		
		this.type=stream.readInt();
		
		if(stream.readBoolean())
		{
			this.pos=new PosData();
			this.pos.readBytesFull(stream);
		}
		else
		{
			this.pos=null;
		}
		
		this.dir=new DirData();
		this.dir.readBytesFull(stream);
		
		if(stream.readBoolean())
		{
			this.realDir=new DirData();
			this.realDir.readBytesFull(stream);
		}
		else
		{
			this.realDir=null;
		}
		
		this.realSpeedRatio=stream.readInt();
		
		stream.endReadObj();
	}
	
	/** 读取字节流(简版) */
	@Override
	protected void toReadBytesSimple(BytesReadStream stream)
	{
		super.toReadBytesSimple(stream);
		
		this.type=stream.readInt();
		
		if(stream.readBoolean())
		{
			this.pos=new PosData();
			this.pos.readBytesSimple(stream);
		}
		else
		{
			this.pos=null;
		}
		
		this.dir=new DirData();
		this.dir.readBytesSimple(stream);
		
		if(stream.readBoolean())
		{
			this.realDir=new DirData();
			this.realDir.readBytesSimple(stream);
		}
		else
		{
			this.realDir=null;
		}
		
		this.realSpeedRatio=stream.readInt();
		
	}
	
	/** 转文本输出 */
	@Override
	protected void toWriteDataString(DataWriter writer)
	{
		super.toWriteDataString(writer);
		
		writer.writeTabs();
		writer.sb.append("type");
		writer.sb.append(':');
		writer.sb.append(this.type);
		
		writer.writeEnter();
		writer.writeTabs();
		writer.sb.append("pos");
		writer.sb.append(':');
		if(this.pos!=null)
		{
			this.pos.writeDataString(writer);
		}
		else
		{
			writer.sb.append("PosData=null");
		}
		
		writer.writeEnter();
		writer.writeTabs();
		writer.sb.append("dir");
		writer.sb.append(':');
		if(this.dir!=null)
		{
			this.dir.writeDataString(writer);
		}
		else
		{
			writer.sb.append("DirData=null");
		}
		
		writer.writeEnter();
		writer.writeTabs();
		writer.sb.append("realDir");
		writer.sb.append(':');
		if(this.realDir!=null)
		{
			this.realDir.writeDataString(writer);
		}
		else
		{
			writer.sb.append("DirData=null");
		}
		
		writer.writeEnter();
		writer.writeTabs();
		writer.sb.append("realSpeedRatio");
		writer.sb.append(':');
		writer.sb.append(this.realSpeedRatio);
		
		writer.writeEnter();
	}
	
	/** 回池 */
	@Override
	protected void toRelease(DataPool pool)
	{
		super.toRelease(pool);
		
		this.type=0;
		this.pos=null;
		this.dir=null;
		this.realDir=null;
		this.realSpeedRatio=0;
	}
	
}
