package com.home.commonSceneBase.net.sceneBaseResponse.unit;
import com.home.commonSceneBase.constlist.generate.SceneBaseResponseType;
import com.home.commonSceneBase.net.sceneBaseResponse.base.CUnitRResponse;
import com.home.shine.bytes.BytesReadStream;
import com.home.shine.support.DataWriter;
import com.home.shine.support.pool.DataPool;

/** 单位操作消息(generated by shine) */
public class CUnitOperateResponse extends CUnitRResponse
{
	/** 数据类型ID */
	public static final int dataID=SceneBaseResponseType.CUnitOperate;
	
	/** 目标操作体实例ID */
	public int targetInstanceID;
	
	/** 参数 */
	public int arg;
	
	public CUnitOperateResponse()
	{
		_dataID=SceneBaseResponseType.CUnitOperate;
	}
	
	/** 获取数据类名 */
	@Override
	public String getDataClassName()
	{
		return "CUnitOperateResponse";
	}
	
	/** 读取字节流(完整版) */
	@Override
	protected void toReadBytesFull(BytesReadStream stream)
	{
		super.toReadBytesFull(stream);
		
		stream.startReadObj();
		
		this.targetInstanceID=stream.readInt();
		
		this.arg=stream.readInt();
		
		stream.endReadObj();
	}
	
	/** 读取字节流(简版) */
	@Override
	protected void toReadBytesSimple(BytesReadStream stream)
	{
		super.toReadBytesSimple(stream);
		
		this.targetInstanceID=stream.readInt();
		
		this.arg=stream.readInt();
		
	}
	
	/** 转文本输出 */
	@Override
	protected void toWriteDataString(DataWriter writer)
	{
		super.toWriteDataString(writer);
		
		writer.writeTabs();
		writer.sb.append("targetInstanceID");
		writer.sb.append(':');
		writer.sb.append(this.targetInstanceID);
		
		writer.writeEnter();
		writer.writeTabs();
		writer.sb.append("arg");
		writer.sb.append(':');
		writer.sb.append(this.arg);
		
		writer.writeEnter();
	}
	
	/** 执行 */
	@Override
	protected void execute()
	{
		scene.role.operate(unit,targetInstanceID);
	}
	
	/** 回池 */
	@Override
	protected void toRelease(DataPool pool)
	{
		super.toRelease(pool);
		
		this.targetInstanceID=0;
		this.arg=0;
	}
	
}
