package com.home.shine.data;
import com.home.shine.bytes.BytesReadStream;
import com.home.shine.bytes.BytesWriteStream;
import com.home.shine.constlist.generate.ShineDataType;
import com.home.shine.data.BaseData;
import com.home.shine.data.TimePassData;

/** (generated by shine) */
public class SecondPassData extends TimePassData
{
	/** 数据类型ID */
	public static final int dataID=ShineDataType.SecondPass;
	
	/** 剩余毫秒数 */
	private int _lastMilli=0;
	
	public SecondPassData()
	{
		_dataID=ShineDataType.SecondPass;
	}
	
	@Override
	public void reStart()
	{
		super.reStart();
		
		_lastMilli=0;
	}
	
	/** 将时间校对到现在时刻 */
	@Override
	protected void toAddTime(int delay)
	{
		_lastMilli+=delay;
		
		if(_lastMilli >= 1000)
		{
			int s=_lastMilli / 1000;
			
			timePass+=s;
			
			_lastMilli-=s * 1000;
		}
	}
	
	/** 获取数据类名 */
	@Override
	public String getDataClassName()
	{
		return "SecondPassData";
	}
	
	/** 读取字节流(完整版) */
	@Override
	protected void toReadBytesFull(BytesReadStream stream)
	{
		super.toReadBytesFull(stream);
		
		stream.startReadObj();
		
		stream.endReadObj();
	}
	
	/** 写入字节流(完整版) */
	@Override
	protected void toWriteBytesFull(BytesWriteStream stream)
	{
		super.toWriteBytesFull(stream);
		
		stream.startWriteObj();
		
		stream.endWriteObj();
	}
	
}
