package com.home.shine.net.request;
import com.home.shine.bytes.BytesWriteStream;
import com.home.shine.constlist.generate.ShineRequestType;
import com.home.shine.control.BytesControl;
import com.home.shine.net.base.ShineRequest;
import com.home.shine.support.DataWriter;
import com.home.shine.support.pool.DataPool;

/** 连接成功消息(generated by shine) */
public class SocketConnectSuccessRequest extends ShineRequest
{
	/** 数据类型ID */
	public static final int dataID=ShineRequestType.SocketConnectSuccess;
	
	/** 连接id */
	public int id;
	
	/** 重连令牌 */
	public int token;
	
	public SocketConnectSuccessRequest()
	{
		_dataID=ShineRequestType.SocketConnectSuccess;
	}
	
	@Override
	protected void copyData()
	{
		super.copyData();
	}
	
	/** 获取数据类名 */
	@Override
	public String getDataClassName()
	{
		return "SocketConnectSuccessRequest";
	}
	
	/** 写入字节流(完整版) */
	@Override
	protected void toWriteBytesFull(BytesWriteStream stream)
	{
		super.toWriteBytesFull(stream);
		
		stream.startWriteObj();
		
		stream.writeInt(this.id);
		
		stream.writeInt(this.token);
		
		stream.endWriteObj();
	}
	
	/** 写入字节流(简版) */
	@Override
	protected void toWriteBytesSimple(BytesWriteStream stream)
	{
		super.toWriteBytesSimple(stream);
		
		stream.writeInt(this.id);
		
		stream.writeInt(this.token);
		
	}
	
	/** 转文本输出 */
	@Override
	protected void toWriteDataString(DataWriter writer)
	{
		super.toWriteDataString(writer);
		
		writer.writeTabs();
		writer.sb.append("id");
		writer.sb.append(':');
		writer.sb.append(this.id);
		
		writer.writeEnter();
		writer.writeTabs();
		writer.sb.append("token");
		writer.sb.append(':');
		writer.sb.append(this.token);
		
		writer.writeEnter();
	}
	
	/** 回池 */
	@Override
	protected void toRelease(DataPool pool)
	{
		super.toRelease(pool);
		
		this.id=0;
		this.token=0;
	}
	
	/** 创建实例 */
	public static SocketConnectSuccessRequest create(int id,int token)
	{
		SocketConnectSuccessRequest re=(SocketConnectSuccessRequest)BytesControl.createRequest(dataID);
		re.id=id;
		re.token=token;
		return re;
	}
	
}
