package com.home.shine.net.request;
import com.home.shine.bytes.BytesWriteStream;
import com.home.shine.constlist.generate.ShineRequestType;
import com.home.shine.control.BytesControl;
import com.home.shine.net.base.ShineRequest;

/** socket重连失败消息(generated by shine) */
public class SocketReconnectFailedRequest extends ShineRequest
{
	/** 数据类型ID */
	public static final int dataID=ShineRequestType.SocketReconnectFailed;
	
	public SocketReconnectFailedRequest()
	{
		_dataID=ShineRequestType.SocketReconnectFailed;
	}
	
	@Override
	protected void copyData()
	{
		super.copyData();
	}
	
	/** 获取数据类名 */
	@Override
	public String getDataClassName()
	{
		return "SocketReconnectFailedRequest";
	}
	
	/** 写入字节流(完整版) */
	@Override
	protected void toWriteBytesFull(BytesWriteStream stream)
	{
		super.toWriteBytesFull(stream);
		
		stream.startWriteObj();
		
		stream.endWriteObj();
	}
	
	/** 创建实例 */
	public static SocketReconnectFailedRequest create()
	{
		SocketReconnectFailedRequest re=(SocketReconnectFailedRequest)BytesControl.createRequest(dataID);
		return re;
	}
	
}
