package com.home.shine.net.response;
import com.home.shine.bytes.BytesReadStream;
import com.home.shine.constlist.generate.ShineResponseType;
import com.home.shine.net.base.ShineResponse;

/** socket重连失败消息(generated by shine) */
public class SocketReconnectFailedResponse extends ShineResponse
{
	/** 数据类型ID */
	public static final int dataID=ShineResponseType.SocketReconnectFailed;
	
	public SocketReconnectFailedResponse()
	{
		_dataID=ShineResponseType.SocketReconnectFailed;
	}
	
	/** 获取数据类名 */
	@Override
	public String getDataClassName()
	{
		return "SocketReconnectFailedResponse";
	}
	
	/** 读取字节流(完整版) */
	@Override
	protected void toReadBytesFull(BytesReadStream stream)
	{
		super.toReadBytesFull(stream);
		
		stream.startReadObj();
		
		stream.endReadObj();
	}
	
	/** 执行 */
	@Override
	protected void execute()
	{
		socket.onReconnectFailed();
	}
	
}
