package com.home.shine.net.response;
import com.home.shine.bytes.BytesReadStream;
import com.home.shine.constlist.generate.ShineResponseType;
import com.home.shine.ctrl.Ctrl;
import com.home.shine.net.base.ShineResponse;
import com.home.shine.net.socket.SendSocket;
import com.home.shine.support.DataWriter;
import com.home.shine.support.pool.DataPool;

/** 重连成功(generated by shine) */
public class SocketReconnectSuccessResponse extends ShineResponse
{
	/** 数据类型ID */
	public static final int dataID=ShineResponseType.SocketReconnectSuccess;
	
	/** 最后收包索引 */
	public int lastReceiveIndex;
	
	public SocketReconnectSuccessResponse()
	{
		_dataID=ShineResponseType.SocketReconnectSuccess;
	}
	
	/** 获取数据类名 */
	@Override
	public String getDataClassName()
	{
		return "SocketReconnectSuccessResponse";
	}
	
	/** 读取字节流(完整版) */
	@Override
	protected void toReadBytesFull(BytesReadStream stream)
	{
		super.toReadBytesFull(stream);
		
		stream.startReadObj();
		
		this.lastReceiveIndex=stream.readInt();
		
		stream.endReadObj();
	}
	
	/** 读取字节流(简版) */
	@Override
	protected void toReadBytesSimple(BytesReadStream stream)
	{
		super.toReadBytesSimple(stream);
		
		this.lastReceiveIndex=stream.readInt();
		
	}
	
	/** 转文本输出 */
	@Override
	protected void toWriteDataString(DataWriter writer)
	{
		super.toWriteDataString(writer);
		
		writer.writeTabs();
		writer.sb.append("lastReceiveIndex");
		writer.sb.append(':');
		writer.sb.append(this.lastReceiveIndex);
		
		writer.writeEnter();
	}
	
	/** 执行 */
	@Override
	protected void execute()
	{
		SendSocket sendSocket=(SendSocket)socket;
		
		if(sendSocket!=null)
		{
			sendSocket.onReconnectSuccess(lastReceiveIndex);
		}
		else
		{
			Ctrl.warnLog("重连返回成功时,不该不是SendSocket");
		}
	}
	
	/** 回池 */
	@Override
	protected void toRelease(DataPool pool)
	{
		super.toRelease(pool);
		
		this.lastReceiveIndex=0;
	}
	
}
